import React, { useState } from 'react';
import LevelBadge from './LevelBadge';

const Card = ({ word, level, pronunciation, meaning, example, example_tr, cardNumber }) => {
  const [showTranslation, setShowTranslation] = useState(false);

  return (
    <div className="bg-card p-6 rounded-lg shadow-lg max-w-md mx-auto">
      <div className="flex justify-between items-start mb-4">
        <div className="w-full text-center">
          <h2 className="text-4xl font-bold text-primary tracking-wide">{word}</h2>
        </div>
        <div className="absolute right-8">
          <LevelBadge level={level} />
        </div>
      </div>

      <div className="text-secondary mb-6 text-center">
        <p className="italic text-lg">{pronunciation}</p>
      </div>

      <div className="mb-6 text-center">
        <p className="text-primary text-2xl font-medium leading-relaxed">{meaning}</p>
      </div>

      {example && (
        <div className="mb-6 text-center relative group">
          <p 
            className="text-secondary text-lg italic leading-relaxed px-4 cursor-help transition-colors duration-200 hover:text-accent"
            onMouseEnter={() => setShowTranslation(true)}
            onMouseLeave={() => setShowTranslation(false)}
          >
            "{example}"
            {showTranslation && example_tr && (
              <span className="absolute left-1/2 transform -translate-x-1/2 -bottom-16 w-8/10 
                bg-[#59C1D9] text-gray-800 p-3 rounded-lg text-base 
                font-medium shadow-lg border border-[#C4A873] 
                transition-opacity duration-200 ease-in-out z-20">
                {example_tr}
              </span>
            )}
          </p>
        </div>
      )}

      <div className="text-right text-secondary text-sm">
        Card #{cardNumber}
      </div>
    </div>
  );
};

export default Card;
