import React from 'react';
import LevelBadge from './LevelBadge';

const Card = ({ word, level, pronunciation, meaning, example, cardNumber }) => {
  return (
    <div className="bg-card p-6 rounded-lg shadow-lg max-w-md mx-auto">
      <div className="flex justify-between items-start mb-4">
        <h2 className="text-3xl font-bold text-primary">{word}</h2>
        <LevelBadge level={level} />
      </div>

      <div className="text-secondary mb-3">
        <p className="italic">{pronunciation}</p>
      </div>

      <div className="mb-4">
        <p className="text-primary font-medium">{meaning}</p>
      </div>

      {example && (
        <div className="mb-4">
          <p className="text-secondary italic">"{example}"</p>
        </div>
      )}

      <div className="text-right text-secondary text-sm">
        Card #{cardNumber}
      </div>
    </div>
  );
};

export default Card;
